vec2 square(inout vec2 num) {
    num = vec2(num.x*num.x-num.y*num.y,2.*num.x*num.y);
    return num;
}
vec2 mul(in vec2 one, in vec2 two) {
    vec2 result = vec2(one.x*two.x-one.y*two.y,one.x*two.y+one.y*two.x);
    return result;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = 5.*uv.x - 2.5;
    uv.y = 3.*uv.y - 1.5;
    vec2 z = vec2(0.,0.);
    vec3 col = vec3(0.,0.,0.);
    for(int i = 0;i < 255; i = i + 1) {
        z = square(z) + uv + mul(z, uv)*vec2(iTime-10.);
        if (length(z) > 5.) {
            float tint = float(i)/16.0;
            col = tint*sin(iTime+vec3(0.,2.,4.));
            break;
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
