// from draft base https://shadertoy.com/view/dtySD1

#define cmul(A,B) ( mat2( A, -(A).y, (A).x ) * (B) ) 

void mainImage( out vec4 O, vec2 u )
{
    O *= 0.;
    for ( int k=0; k < 9; k++ ) {                  // AA loop

        vec2  R = iResolution.xy,
              d = vec2(k%3-1, k/3-1) / 3.,         // AA oversampling
              z = 1.23* ( 2.*(u+d) - R )  / R.y,   // windowing
              c = vec2( .346, .06 ); 
        float r = 1.326, N = 144.;

        for ( float i = 0. ; i < N && dot(z,z) < r*r; i++ ) // Julia loop
            z = cmul(z,z) + c;

        O += step( z.x , 0. );                     // binary decomposition
    }
    O = sqrt(O/9.);                                // to sRGB

 // O = vec4( sqrt(.5 - .5* z.x / fwidth(z.x)) );  // variant without the AA loop
 }
