void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y * sin(iTime / 3.0) * 3.0;

    uv = fract(fract(uv) - 0.5 + uv * 2.0 * pow(uv.x, 4.0) * pow(uv.y, 4.0)) - 0.5;

    vec3 col = vec3(cos(length(uv) + sin(iTime)*10.0), sin(length(uv) + sin(iTime)*10.0), 0.1);

    float d = length(uv);    

    d = sin(d*8.0 + sin(iTime / 3.0)*40.0) / 8.0;
    d = abs(d);

    d = 0.03 / d;

    fragColor = vec4(d*col, 1.0);
}
