float plot(vec2 uv, float p, float w){
  return step( p-w, uv.y) - step( p+w, uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;

    vec3 finalColor = vec3(0.0);

    // WAVE 1
    float freq = 234.0;
    float spd = 0.1;

    float y = sin(uv.x * freq + spd * iTime);

    vec3 color = vec3(y);

    float p = plot(uv,y, 0.01);
    vec3 thing = abs(uv.xxx);
    color = p*vec3(thing);


    // WAVE 2
    float amp2 = 0.5;
    float freq2 = 223.0;
    float spd2 = 0.8;

    float y2 = amp2 * sin(uv.x * freq2 + spd2 * iTime);

    vec3 color2 = vec3(y2);

    float p2 = plot(uv,y2, 0.8);
    color2 = p2*vec3(uv.y,0.6,1.0);


    finalColor += color;
    finalColor += color2;

    // Output to screen
    fragColor = vec4(finalColor,1.0);
}
