void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
      vec2 uv = fragCoord;
  // Output to screen
  fragColor = vec4(255.0,0.0,0.0,1.0);
  float sectorStep = 2.0 * 3.14 / 360.0;
  vec4 topLinePoint[360];
  for (int i = 0; i <= 360; i++) {
    float th = float(i)*sectorStep;
    vec4 point = vec4(
      (-10.0 * 10.0*cos(th)) / (10.0*sin(th) + 3.0) + 404.5,
      10.0 / (10.0*sin(th) + 3.0) + 224.5,
      -6.0 - 8.0 / (10.0*sin(th) + 3.0),
      1
    );
    topLinePoint[i]=point;
  }
  bool flag = false;
  for(int i=0; i<360;i++){
    float x = topLinePoint[i][0];   
    float topx = ceil(x);
    float bottomx = floor(x);
    float y = topLinePoint[i][1];
    float topy = ceil(y);
    float bottomy = floor(y);
    if((bottomx <fragCoord.xy.x)&&(fragCoord.xy.x<topx)&&(bottomy < gl_FragCoord.xy.y)&&(gl_FragCoord.xy.y<topy) ){
      flag = true;
    }
  }

  vec3 color;

  if(flag){
    color = vec3(0.0,255.0,0.0);
  }else{
    color = vec3(255.0,255.0,255.0);
  }
  fragColor = vec4(color,1.0);
}
