float iso(vec2 v)
{
    v.y += 0.01;
    v.x += sin(v.y * 20.0 + iTime * 0.1) * 0.2;
    return length(v) - 0.4;
}

vec2 grad(vec2 v)
{
    float E = 0.00000002 * exp(1.25 * sin(iTime * 5.0));
    float c = iso(v);
    float x = iso(v + vec2(E, 0)) - iso(v - vec2(E, 0));
    float y = iso(v + vec2(0, E)) - iso(v - vec2(0, E));
    return vec2(x, y) / E;
}

float dist(vec2 v)
{
    float i = iso(v);
    vec2 g = grad(v);
    return abs(i) / length(g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 moviecol;

    vec2 uvOffset = texture(iChannel1, vec2(iTime*5.0)).rg;
    uvOffset.x *= 0.04;
    uvOffset.y *= 0.052;

    float d = dist(vec2(uv.x, 1.0 - uv.y));

    //moviecol.r = texture(iChannel0, uv + uvOffset + vec2(-0.02*texture(iChannel1, vec2(uv.x,uv.y/200.0 + iTime*5.0)).r, (tan(sin(iTime)) * 0.6 ) * 0.05) ).r;
    moviecol.r = texture(iChannel0, uv + uvOffset + 0.03*smoothstep(0.02, 0.022, d) ).r;
    moviecol.g = vec4(texture(iChannel0, uv + uvOffset)).g;
    moviecol.b = texture(iChannel0, uv / uvOffset + vec2(-0.01*texture(iChannel1, vec2(uv.x/2.0,uv.y + iTime*5.0)).r, -0.2) ).b;

    //moviecol.r = mix(0.8, moviecol.r, smoothstep(0.02, 0.022, d));
    moviecol.rgb = mix(moviecol.rgb, vec3(dot(moviecol.rgb, vec3(.43))), 0.5);

	fragColor = vec4(moviecol);
}
