float sawtooth(float T)
{
    float t = 10.0;
    return (2.0 * ((t / T) - floor(0.5 + (t / T))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv += vec2(0.5,0.5);

    float t = float(iFrame) / 200.0;
    //uv = uv + vec2(sin(t) / 2.0,cos(t) / 2.0);
    //uv.x = uv.x + tan(t / 1000.0);
    //uv.y = uv.y + cos(t / 1000.0);
    //uv.x += float(iFrame)/10000.0;
    uv.x = uv.x * sin(uv.y * t * 2.0);
    uv.y = uv.y * sin(uv.x * t) + (t*t)/10000.0;

    float zoom = 100.0;
    float thresh = 0.99;
    float x_grid = sin(uv.x * zoom);
    float y_grid = cos(uv.y * zoom);
    bool grid = x_grid < thresh && y_grid < thresh;
    float i = 0.0;
    float j = 0.0;
    float k = 0.0;
    if(!grid) {
        i = uv.x;
        j = uv.y;
        k = 1.0;
    }

    vec3 col = vec3(i,j,k);

    // Output to screen
    fragColor = vec4(col,1.0);
}
