void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;

    //warpedUv.x = tan(uv.y * 10.0);
    float start = fract(iTime * 0.2);
    float end = start + 0.15;

    if(uv.y > start && uv.y < end)
    {
        warpedUv.x = uv.x - tan(uv.y * 200.0) * 0.05 - sin(iTime * 200.0) * 0.1;
    }  

    vec2 offset1 = vec2(0.01);
    vec2 offset2 = vec2(0.02);
    vec2 offset3 = vec2(0.03);

    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;

    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
