#define X_MIN 0.0
#define X_MAX 0.8
#define Y_MIN 0.42
#define Y_MAX 0.65

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 origUV = fragCoord.xy / iResolution.xy;

    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

	float frame = floor( mod( iTime*10.0, 6.0 ) );

    //vec2 offset = vec2(0.2);
    //float scale = 3.;

	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame)*nyanWidthUV;


	vec4 nyan = texture( iChannel0, uv );
	vec3 col = nyan.xyz * nyan.a;

    float x = origUV.x - X_MIN; x /= X_MAX - X_MIN;
    if (x >= 0.0 && x <= 1.0 && nyan.a == 0.0)
    {
        x += iTime * 0.1;
        x = x * 5.0 - trunc(x * 5.0) - 0.5;
        if (x>0.0) origUV.y += 0.02;
        else origUV.y -= 0.02;
        float y = origUV.y - Y_MIN; y /= Y_MAX - Y_MIN;
        if (y >= 0.0 && y <= 1.0)
        {
            col.r = 1.0;
            col.g = sin((y+iTime*0.1)*20.0);
            col.b = sin((y-iTime*0.1)*20.0);
        }
    }

    fragColor = vec4( col, 1.0 );
}
