#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) texture(iChannel2,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)

vec4 map (vec2 u) {


    for (float i = 0.; i < 100.; i++) {

        //u = vec2(u.x*u.x-u.y*u.y,2.*u.x*u.y)-vec2(.45,0.);

        u.x = abs(u.x)-.2;
        float a = 5.12;
        u *= mat2(cos(a),sin(a),-sin(a),cos(a));
        u = .2*u/dot(u,u);

    }

    return atan(length(u))*vec4(1);

}

Main {
    if (iFrame < 1||iMouse.z>0.) {
        Q = vec4(0);
        #define aa 2
        for (int x = -aa; x <= aa; x++)
        for (int y = -aa; y <= aa; y++)
        {
            vec2 u = 2.8*(U+vec2(x,y)/float(aa*2)-.5*R)/R.y;
            u.y += 0.38;
            Q += map(u)/float((aa*2+1)*(aa*2+1));
        }
    } else {

        Q = A(U);

    }
}

Main {
    #define aa 2
    Q = vec4(0);
    for (int x = 0; x < aa; x++)
    for (int y = 0; y < aa; y++)
    {
        vec2 u = vec2(x,y)/float(aa*2)*2.-1.;
        #define X 5.
        vec4 n = A(U+u+vec2(0,1))+.3*C(X*(U+u)+vec2(0,1))+.6*A(X*(U+u)+vec2(0,1));
        vec4 e = A(U+u+vec2(1,0))+.3*C(X*(U+u)+vec2(1,0))+.6*A(X*(U+u)+vec2(1,0));
        vec4 s = A(U+u-vec2(0,1))+.3*C(X*(U+u)-vec2(0,1))+.6*A(X*(U+u)-vec2(0,1));
        vec4 w = A(U+u-vec2(1,0))+.3*C(X*(U+u)-vec2(1,0))+.6*A(X*(U+u)-vec2(1,0));

        vec2 g = vec2(e.x-w.x,n.x-s.x);

        vec3 no = normalize(vec3(g,.5));

        Q += (.4+1.3*A(U+u))*C(X*(U+u))*(1.-dot(no,normalize(vec3(cos(iTime),sin(iTime),1))))/float(aa*aa);

    }

}
