void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 col;

    // Time varying pixel colo

    if(int(iTime)%2 == 0){
        col = vec3(1.0, 0.5, 1.0);
        }
    else{
        col = vec3(0.0, 0.5, 1.0);
    }

    if(int(fragCoord.x)%50 > 25){
        if(int(fragCoord.y)%50 > 25){
            col += vec3(0.5, 0.0, 0.0);
            }
        else{
            col += vec3(0.0, 0.0, -0.5);
            }
    }
    else{
        if(int(fragCoord.y)%50 > 25){
            col += vec3(0.0, 0.0, -0.5);
            }
        else{
            col += vec3(0.5, 0.0, 0.0);
            }
    }

    // Output to screen
    fragColor = vec4(col,0.0);
}
