// factors for the effect
float edge_thickness = 1.0;
float edge_brightness = 4.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 c[9];
	for (int i=0; i < 3; ++i) for (int j=0; j < 3; ++j)
	{
        c[3*i+j] = texture(iChannel0, (fragCoord.xy + edge_thickness * vec2(i-1,j-1)) / iResolution.xy).rgb;
	}
	/* adjacent texel array for texel[4]
    036
    147
    258
    */
	vec3 dx = 2.0 * abs(c[7]-c[1]) + abs(c[2] - c[6]) + abs(c[8] - c[0]);
	vec3 dy = 2.0 * abs(c[3]-c[5]) + abs(c[6] - c[8]) + abs(c[0] - c[2]);
    float delta = length(0.25 * (dx+dy) * 0.5);
	vec3 color_edge = clamp(edge_brightness * delta, 0.0, 1.0) * c[4];


    float alpha = 0.5 * (sin(iTime) + 1.);

	fragColor = vec4(mix(color_edge, c[4], alpha), 1.0);
}
