//#define r  mat2(cos( iTime +vec4(.5,33.5,11.5,0)))

#define r  mat2(cos( iTime*.1 +vec4(0,33,11,0)))


void mainImage( out vec4 O, vec2 U ) {

    bool esq = int(U.y)%2==0; 

    vec2  A;
    vec3  R = iResolution, P;
          U = ( U+U - R.xy ) / R.y;                                // normalize coordinates
    float s = .03565,                                              // s+s = 2Pi/72 = 5Â° steps
          z = 1. - dot(U,U);

    // bg color is black
    O *= 0.; 
    //O = vec4(.5,.5,0., 0.); 

    // if on sphere
    z > 0.                                                         
      ? 
        // 3D point on front half-sphere 
        P = vec3( U, pow(z,6.) ),          

        // paralax for interleaved lines stereo monitor
        //P.x += .001*(esq?-1.:+1.),

        P.x += -.5*(esq?.1:-.1),

        // rotate on sphere
        P.yz *= r,
        P.xz *= r,  



        A = vec2( atan(length(P.xy),P.z), atan(P.x,P.y) ),        // get parameterization
        O.g = length( ( mod( A+s, s+s ) -s) 
                       // / fwidth(A) ) * 50./R.y               
                       //* inverse(transpose(mat2(dFdx(A),dFdy(A)))) )  * 1e2/R.y  // ( precise )
                       //* inverse(transpose(mat2(dFdx(A),dFdy(A)))) )  * 1e2/R.y  // ( precise )
                       * 1e4/R.y)
      : s;
}
