const int inputSeed = 2;

uint rngstate = uint(0);

uint randInt() {
    rngstate ^= rngstate << 13;
    rngstate ^= rngstate >> 17;
	rngstate ^= rngstate << 5;
    return rngstate;
}

void setSeed(uint seed) {
    rngstate = seed;
    randInt(); randInt(); randInt(); // Shift some bits around
}

float randFloat() {
    return fract(float(randInt()) / float(1<<32 - 5));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    setSeed(uint(inputSeed) + uint((fragCoord.y * iResolution.y + fragCoord.x) * fragCoord.x));
    fragColor = vec4(vec3(randFloat()),1.0);
}
