vec3 RED = vec3(1.0, 0.0, 0.0);
vec3 GREEN = vec3(0.0, 1.0, 0.0);
vec3 BLUE = vec3(0.0, 0.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
    st.x -= 0.4;

    float pct = 0.0;

    float c1 = distance(st,vec2(.5 + cos(iTime * .5) * .1, .5 + sin(iTime * .5) * .3));
    float c2 = distance(st,vec2(.5 + cos(iTime) * .3, .5 + sin(iTime) * .1));
    float c3 = distance(st,vec2(.5 + cos(iTime * 1.5) * .3, .5 + sin(iTime * 1.5) * .3));
    float c4 = distance(st,vec2(.5 + cos(iTime * .2) * .2, .5 + sin(iTime * .2) * .2));
    float c5 = distance(st,vec2(.5 + cos(iTime) * .3, .5 + sin(iTime) * .1));
    float c6 = distance(st,vec2(.5 + cos(iTime * 1.5) * .3, .5 + sin(iTime * 1.5) * .3));

    pct = c1 * c2 * c3 * c4;

    pct = 1. - step(pct, 0.002);

    vec3 color = vec3(pct) + c1 * RED + c2 * GREEN + c3 * BLUE + c4 * vec3(1.);

    color = 1. - color;

    fragColor = vec4(color,1.0);
}
