void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;
    float zoom = 10.0;
    uv *= zoom;

    // |x/a|^n + |y/b|^n = 1
    float x, y, a, b, n, squareness; // rectangularity
    x = uv.x;
    y = uv.y;
    a = 2.0;
    b = 4.0;
    squareness = 1.0;
    n = (cos(iTime) + 1.0) / 2.0 * squareness;
    x = pow(abs(x / a), n);
    y = pow(abs(y / b), n);
    vec3 col = vec3(0.05f, 0.1f, 0.15f);
    if (x + y <= 1.0)
    {
        col = vec3(1.0, 0.87, 0.33);
    }
    fragColor = vec4(col,1.0);
}
