//No03_WhiteCrossMouse

#define W 1.2
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  float mx = iMouse.x > 1.0 ? iMouse.x : iResolution.x / 2.0;
  float my = iMouse.y > 1.0 ? iMouse.y : iResolution.y / 2.0;
  vec3 col = vec3(   abs(fragCoord.x - mx) > W
                  && abs(fragCoord.y - my) > W );
  fragColor = vec4(col,1.0);
}
