    vec2 yp = vec2(ypx, ypy);
    // Output to screen
    col.g += (xpx*xpy) + (ypx*ypy);

    vec2 xyd = xp+yp/2.;
    //col.r += xyd.x * xyd.y;
    fragColor = vec4(col,1.0);
}
