vec3 palette1( float t ) {
    //by Inigo Quilez
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(0.5,0.5,0.5);
    vec3 d = vec3(0.1,0.1,0.2);

    return vec3(a + b*cos(6.28318530718*(c*(t) + d)));
}

vec3 palette2( float t ) {
    //by Inigo Quilez
    vec3 a = vec3(0.5,0.6,0.5);
    vec3 b = vec3(0.5,0.4,0.25+0.25*cos(iTime*3.0));
    vec3 c = vec3(1.0,0.3+0.2*sin(iTime),1.2);
    vec3 d = vec3(0.5+0.5*cos(iTime/3.0)*1.0,0.1,0.8);

    return vec3(a + b*cos(6.28318530718*(c*(t) + d)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float scaling = max(1.0,iTime/3.0);
    vec2 uv = (fragCoord/iResolution.xy - 0.5)*scaling;
    vec3 color1 = palette1(6.28*length(uv)/scaling + iTime);

    uv = exp(-abs(uv)/scaling)*cos(uv);
    vec3 color2 = palette2(6.28*length(uv+iTime/20.0)/scaling);


    uv = atan(uv);
    vec3 color3 = palette1(length(exp(uv*(0.5+0.5*cos(iTime*uv.x))+uv.y*uv.x/iTime)));

    uv = tan(iTime+exp(-uv));


    vec3 finalColor = vec3(0.0);
    float weight1 = 0.3 +0.2*cos(iTime*uv.x*uv.y);
    float weight2 = 0.2 + 0.3*sin(iTime*uv.y/1.0); 
    finalColor += weight1*color1 + weight2*color2 + (1.0-weight1-weight2)*color3;
    // Output to screen
    fragColor = vec4(finalColor,1.0);
}
