// Fork of "Shrinking Circles 2" by Wheat. https://shadertoy.com/view/msffDS
// 2023-07-11 23:10:32

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set scale; sizes to y resolution
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // find points a certain distance from circle w/ .5 radius
        // float d = length(uv);
        // d = sin(d * 8.0 + iTime) / 8.0;
        // d = abs(d);

    // make a spiral (angle from x-axis: atan(y/x))
    float tightness = 1.;
    float speed = .6;
    float sufficientlyLargeFloat = 100.;
    float numPins = 8.0; // keep even
    // makes distance from center, angle from x-axis,
    // and time factors
    float d = length(uv)
        + atan(uv.y / uv.x) / tightness
        + iTime * speed
        + sufficientlyLargeFloat;
    d = abs(d);
    // makes it repeat
    d = sin(d*numPins)/8.0;

    d = smoothstep(0.0, .1, d);

    vec3 col = vec3(d,d,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
