const float M_PI = 3.14159265358979323846264338327950288;

const int[80] NUMBERS = int[80](0x00,0x3E,0x41,0x41,0x41,0x3E,0x00,0x00,
                                0x00,0x00,0x00,0x21,0x7F,0x01,0x00,0x00,
