                                0x00,0x72,0x51,0x51,0x51,0x4E,0x00,0x00,
                                0x00,0x3E,0x49,0x49,0x49,0x26,0x00,0x00,
                                0x00,0x40,0x40,0x40,0x4F,0x70,0x00,0x00,
                                0x00,0x36,0x49,0x49,0x49,0x36,0x00,0x00,
                                0x00,0x32,0x49,0x49,0x49,0x3E,0x00,0x00);

#define D(s)   ( ( NUMBERS[(s)*8 + int(ipos.x)] & (0x1 << int(ipos.y)) ) >> int(ipos.y))


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 8.0;
    vec2 ipos = floor(uv);
    //vec2 fpos = fract(uv);

    float tt = mod(iTime, 10.0);
    float fTime = fract(tt);

    int currentSec = int(tt);
    int nextSec = (currentSec+1)%10;

    float x = fTime * 2.0 * M_PI;
    float y = (cos(x)+1.0) / 2.0;

    fragColor = vec4(y*vec2( D( fTime<.5 ? currentSec : nextSec)), 0, 1);

}
