vec4 circle(vec2 uv, float r, vec2 pos) {
    float cx = uv.x-pos.x;
    float cy = uv.y-pos.y;

    if ((cx*cx)+(cy*cy) < r/100.) {
        return vec4(.7, .7, .75, 0.);
    } else {
        return vec4(.2, .2, .25, 0.);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 pos = iMouse.xy/iResolution.y;
    float r = 1.;

    fragColor = circle(uv, r, pos);
}
