// The noise part of this code was borrowed. See credit below.

// If step size is too low you get visible undersampling artifacts.
// But arguably they are still pretty :)
float step_size = 0.0005;//0.0005
float h = 400.;//400.
float sx = 3.0;
float sy = 3.0;
float rate = 1.0;
float intensity = 500.;//500.
float range = 5.;//5

//
// Description : Array and textureless GLSL 2D/3D/4D simplex 
//               noise functions.
//      Author : Ian McEwan, Ashima Arts.
//  Maintainer : stegu
//     Lastmod : 20201014 (stegu)
//     License : Copyright (C) 2011 Ashima Arts. All rights reserved.
//               Distributed under the MIT License. See LICENSE file.
//               https://github.com/ashima/webgl-noise
//               https://github.com/stegu/webgl-noise
// 

vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 mod289(vec4 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec4 permute(vec4 x) {
     return mod289(((x*34.0)+10.0)*x);
}

vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v)
  { 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //   x0 = x0 - 0.0 + 0.0 * C.xxx;
  //   x1 = x0 - i1  + 1.0 * C.xxx;
  //   x2 = x0 - i2  + 2.0 * C.xxx;
  //   x3 = x0 - 1.0 + 3.0 * C.xxx;
  vec3 x1 = x0 - i1 + C.xxx;
  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
  i = mod289(i); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.5 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 105.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}

//
// Description : Array and textureless GLSL 2D/3D/4D simplex
//               noise functions.
//      Author : Ian McEwan, Ashima Arts.
//  Maintainer : ijm
//     Lastmod : 20110822 (ijm)
//     License : Copyright (C) 2011 Ashima Arts. All rights reserved.
//               Distributed under the MIT License. See LICENSE file.
//               https://github.com/ashima/webgl-noise
//

float mod289(float x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0; }

float permute(float x) {
     return mod289(((x*34.0)+1.0)*x);
}


float taylorInvSqrt(float r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

//////////////////////////////////////
// My code
//////////////////////////////////////
float window(float z)
{
    return 0.5 + 0.5 * sin(2. * 3.14159 * (z - 0.25));
}

float f(float a0, float a1, float a2, float a3, float a4, float a5, float z)
{
    return a0+z*(a1+z*(a2+z*(a3+z*(a4+a5*z))));
}

float df(float a0, float a1, float a2, float a3, float a4, float a5, float z)
{
    return a1+z*(2.*a2+z*(3.*a3+z*(4.*a4+5.*a5*z)));
}

float disc(float a0, float a1, float a2, float a3, float a4)
{
return a4*a4 *(-9.*a2*a2*a3*a3 + 32.*a2*a2*a2*a4 - 108.*a1*a2*a3*a4 + 
   27.*a1*(a3*a3*a3 + 4.*a1*a4*a4));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;
    float x = uv.x;
    float y = uv.y;

    float c = 0.0;
    float s = 0.0;
//    float k = 0.;
//    float l = 0.;
    int count = 0;

    float tOffset = 10. * iMouse.x/iResolution.x;

    float a0 = snoise(vec3(sx*x, sy*y, tOffset + rate * iTime + 0.));
    float a1 = snoise(vec3(sx*x, 100.0+sy*y, tOffset + rate * iTime + 12.1));
    float a2 = snoise(vec3(sx*x, sy*y, tOffset + rate * iTime + 19.2));
    float a3 = snoise(vec3(sx*x, sy*y, tOffset + rate * iTime + 75.7));
    float a4 = snoise(vec3(sx*x, sy*y, tOffset + rate * iTime + 113.2));
    float a5 = 0.;//snoise(vec3(62.5+sx*x, sy*y, tOffset + rate * iTime + 142.1));

    float last_dtheta = 0.;
    int stationaries = 0;
    for (float z = 0.; z < 1.; z += step_size)
    {
        float zz = range * z - 0.5 * range;
        float xx = 10.*x-5.;
        float yy = 10.*y -5.;

        if (false)
        {
            // Pure Pearcey
            // But reduce h to 80. or so
            // if you enable this
            a0 = 0.;
            a1 = xx;
            a2 = yy;
            a3 = 0.;
            a4 = 1.;
        }
        a5 = 0.;

        float theta = f(a0,a1,a2,a3,a4,a5,zz);
        c = c + window(z)*cos(h*theta);
        s = s + window(z)*sin(h*theta);
        float dtheta = df(a0,a1,a2,a3,a4,a5,zz);
        if (dtheta == 0.)
        {
            ++stationaries;
        }
        if (dtheta * last_dtheta < 0.)
        {
            ++stationaries;
        }
        last_dtheta = dtheta;

        count += 1;
    }
    float ss = float(stationaries);
    ss = 1.+2.*float(disc(a0,a1,a2,a3,a4)<0.);
    c = intensity * (c*c+s*s)/float(count*count);
    vec3 col = vec3(c, 0.333*ss, c);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
