void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 tex = texture(iChannel0, uv);
    vec4 tex_b = texture(iChannel0,  vec2(
        mix(pow(uv.x + .219, 2.),mix(uv.x, pow(uv.x - 1.2, 2.) * -1. + .99, step(.9, uv.x)), step(.1, uv.x)),
        mix(pow(uv.y + .219, 2.),mix(uv.y, pow(uv.y - 1.2, 2.) * -1. + .99, step(.9, uv.y)), step(.1, uv.y))
    ));
    vec4 tex_r = texture(iChannel0, vec2(
        mix(pow(uv.x + .1 , 2.) + .06 ,mix(uv.x, pow(uv.x - 1.1, 2.) * -1.2 + .95, step(.9, uv.x)), step(.1, uv.x)),
        mix(pow(uv.y + .1 , 2.) + .06 ,mix(uv.y, pow(uv.y - 1.1, 2.) * -1.2 + .95, step(.9, uv.y)), step(.1, uv.y))
    ));


    // Output to screen
    fragColor = vec4(tex.r, tex_r.g, tex_b.b,1.0);
}
