void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5)/iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - iResolution.xy*0.5)/iResolution.y;

    if(iMouse.w == 0.){
        o_mou_coo_nor = vec2(sin(iTime)*0.3, cos(iTime)*0.3);
    }
    //float n_freqs = o_mou_coo_nor.x*1000.0;
    float n_subdivisions = 5.;
    vec2 o_opcn_big = o_pix_coo_nor*vec2(n_subdivisions);
    vec2 o_opcn_big_mod = fract(o_opcn_big);
    vec2 o_opcn_big_floor = floor(o_opcn_big);

    vec2 o_mpcn_big = o_mou_coo_nor*vec2(n_subdivisions);
    vec2 o_mpcn_big_mod = fract(o_mpcn_big);
    vec2 o_mpcn_big_floor = floor(o_mpcn_big);

    float n_dist = length(o_mpcn_big_mod - o_opcn_big_mod);
    float n_dist2 = length(o_mpcn_big - o_opcn_big);
    float n_dist3 = length(o_pix_coo_nor - o_mou_coo_nor);
    n_dist3 = pow(n_dist3, 0.5);
    float n_inside = float(o_mpcn_big_floor == o_opcn_big_floor);
    vec2 n_col_mask = o_mpcn_big_mod / n_subdivisions;


    fragColor = vec4((1.-(n_dist3*n_subdivisions/2.))*n_inside);

}
