void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // set scale; sizes to y resolution
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    // find points a certain distance from circle w/ .5 radius
    float d = length(uv);
    d = sin(d * 8.0 + iTime * 10.0) / 8.0;
    d = abs(d) * 2.0;

    // 
    d = smoothstep(0.0, .1, d);

    vec3 col = vec3(d,d,d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
