mat2 rotate2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
o=vec4(0.);
vec2 r = iResolution.xy;
float t = iTime;

float i,j,d=.2,e=.1;
for (i=0.;i++<64.&&d>1e-4;){
    vec3 p=vec3((FC.xy-.5*r)/r.y,e);
    p.z-=4.;p.yz*=rotate2D(t*.1);
    for(j=0.;j++<16.;)
        p=abs(abs(p)-.1)-.05,
        p.xy=fract(p.xy),
        d=min(min(p.x,p.z),abs(p.y))-.003,
        d-=dot(p,p)/2e4;
    e+=d;
}o+=3./i;
}
