void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 cFront=vec3(1.,0.,0.);
    vec3 cBack=vec3(0.,0.,1.);

    vec2 ptStart=vec2(320.1,18.);
    vec2 ptEnd=vec2(310.1,300.);
    vec2 pt0=ptEnd-ptStart;

    if(pt0.x==0.&&pt0.y==0.) return;

    if(pt0.x==0.){
        if(ptEnd.x-fragCoord.x<=0.5&&fragCoord.x-ptEnd.x<0.5) fragColor=vec4(cFront,1.);
    }else if(pt0.y==0.){
        if(ptEnd.y-fragCoord.y<=0.5&&fragCoord.y-ptEnd.y<0.5) fragColor=vec4(cFront,1.);
    }else{
        if(pt0.x<=pt0.y){
            if(min(ptStart.y,ptEnd.y)<=fragCoord.y&&max(ptStart.y,ptEnd.y)>=fragCoord.y){
                float k=pt0.x/pt0.y;
                float b=ptEnd.x-ptEnd.y*k;
                float xNow=fragCoord.y*k+b;
                if(xNow-fragCoord.x<=0.5&&fragCoord.x-xNow<0.5) fragColor=vec4(cFront,1.);
            }
        }else{
            if(min(ptStart.x,ptEnd.x)<=fragCoord.x&&max(ptStart.x,ptEnd.x)>=fragCoord.x){
                float k=pt0.y/pt0.x;
                float b=ptEnd.y-ptEnd.x*k;
                float yNow=fragCoord.x*k+b;
                if(yNow-fragCoord.y<=0.5&&fragCoord.y-yNow<0.5) fragColor=vec4(cFront,1.);
            }
        }
    }
}
