vec3 palette( float t, vec3 a, vec3 b, vec3 c, vec3 d) {
    return a + b*cos(6.28318*(c*t+d));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // warping effect on bottom and left
    //             \/\/ increase those numbers by factor of 10 for crazy warping
    fragCoord.x +=(1000.+iTime)/(10.*mod(fragCoord.x, iResolution.x));
    fragCoord.y +=(1000.+iTime)/(10.*mod(fragCoord.y, iResolution.y));

    // Rotating the circles
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    uv.x = uv.x*cos(mod(iTime/2.,6.28))-uv.y*sin(mod(iTime/2.,6.28));
    uv.y = uv.y*cos(mod(iTime/2.,6.28))+uv.x*sin(mod(iTime/2.,6.28));

    // Rotating the background color
    vec2 uv0 = uv;
    uv0.x = uv0.x*cos(mod(iTime/2.,6.28))+uv0.y*sin(mod(iTime/2.,6.28));
    uv0.y = uv0.y*cos(mod(iTime/2.,6.28))-uv0.x*sin(mod(iTime/2.,6.28));
    uv=fract(uv*1.5) - 0.5;

    // Making circles go to lines and back
    uv.x = uv.x*sin(mod(iTime,12.56)/2.);
    uv.y = uv.y*cos(mod(iTime,12.56)/2.);

    // Color changing per circle
    float d = length(uv);
    vec3 col = palette(sin(length(uv0)-d+iTime)-length(uv0), vec3(0.500, 0.500, 0.500), vec3(0.408, 0.500, 0.500), vec3(0.938, -1.822, 0.228), vec3(0.907, 2.017, 2.857));

    // More color stuff
    d = sin(d*16. - iTime)/16.;
    d = abs(d);
    d = 0.02/d;
    col *= d;    
    fragColor = vec4(col, 1.0);
}
