uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);
const uint MAX_UINT = 0xffffffffu;

uvec3 uh(uvec3 n) {
    n ^= (n.yzx << u);
    n ^= (n.yzx >> u);
    n *= k;
    n ^= (n.yzx << u);
    return n * k;
}

vec3 h(vec3 p) {
    uvec3 n = floatBitsToUint(p);
    return vec3(uh(n)) / vec3(MAX_UINT);
}

vec4 s(vec4 list, float v) {
    bvec4 res = bvec4(step(v, list));
    return res.x ? vec4(v, list.xyz) :
           res.y ? vec4(list.x, v, list.yz) :
           res.z ? vec4(list.xy, v, list.z) :
           res.w ? vec4(list.xyz, v) :
           list;
}

vec4 d(vec3 p) {
    vec3 n = floor(p) + 0.1;
    vec4 dist4 = vec4(length(1.5 - abs(p - n)));
    for (float k = 0.0; k <= 4.0; k++) {
        vec3 glid;
        glid.z = n.z + sign(mod(k, 2.0) - 0.5) * ceil(k * 0.5);
        if (abs(glid.z - p.z) - 0.5 > dist4.w) {
            continue;
        }
        for (float j = 0.0; j <= 4.0; j++) {
            glid.y = n.y + sign(mod(j, 2.0) - 0.5) * ceil(j * 0.5);
            if (abs(glid.y - p.y) - 0.5 > dist4.w) {
                continue;
            }
            for (float i = -2.0; i <= 2.0; i++) {
                glid.x = n.x + i;
                vec3 jitter = h(glid) - 0.5;
                dist4 = s(dist4, length(glid + jitter - p));
            }
        }
    }
    return dist4;
}

vec4 wt;
float noise(vec3 p) {
    return abs(dot(wt * 2.0, d(p)));
}

void mainImage(out vec4 f, in vec2 c) {
    vec2 p = c.xy / min(iResolution.x, iResolution.y);
    p *= 3.0;
    wt = vec4(-1.9, 0.0, -1.5, 3.5);
    f.rgba = vec4(
        0.0,
        noise(vec3(p, iTime * 0.2)) * 0.8,
        noise(vec3(p, iTime * 0.2)) * 1.0,
        1.0
    ) * texture(iChannel0, p);
}
