//const float n_tau = 6.28318530717958647;
float f_n_difference_to_line_function(
    vec2 a_n_start, 
    vec2 a_n_end, 
    vec2 a_n_pixel_coord_normalized
){
    //remember that math lesson ?
    // y = m*x+q
    float n_delta_y = a_n_end.y - a_n_start.y; 
    float n_delta_x = a_n_end.x - a_n_start.x; 
    float n_m = n_delta_y / n_delta_x;
    float n_q = -(n_m*a_n_start.x) + a_n_start.y;

    float n_x = a_n_pixel_coord_normalized.x;

    float n_y = n_m * n_x + n_q; 

    float n_difference = abs(a_n_pixel_coord_normalized.y - n_y);
    return n_difference;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(1.0);
    float n_zoomout = 3.0;
    vec2 a_n_pixel_pos_normalized = n_zoomout*(fragCoord.xy-iResolution.xy*0.5) / iResolution.y;

    vec2 o_p1 = vec2(-0.2,0.8);
    vec2 o_p2 = vec2(-0.5, 0.2); 

    float n_brightness = 0.0;
    float n_difference_to_line_function = f_n_difference_to_line_function(
        o_p1, 
        o_p2, 
        a_n_pixel_pos_normalized
    );

    fragColor *= vec4(abs(n_difference_to_line_function*20.00));
    fragColor *= vec4((smoothstep(0.1,0.11,length(a_n_pixel_pos_normalized-o_p1))));
    fragColor *= vec4((smoothstep(0.1,0.11,length(a_n_pixel_pos_normalized-o_p2))));

}
