vec2 barrelDistortion(vec2 uv)
{   
    // ratchet this up to create a more pronounced barrel
    float distortion = 0.2;
    float r = uv.x*uv.x + uv.y*uv.y;
    uv *= 1.6 + distortion * r + distortion * r * r;

    // flipping the x axis
    uv.x = 1. - uv.x;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv = barrelDistortion(uv);
    uv = 0.5 * (uv * 0.5 + 1.0);

    // uncomment this to view barrel distortion
	// fragColor = texture(iChannel0, uv);

    // svg filter fragcolor
	fragColor = vec4(uv.x, 0, uv.y, 1);
}
