# define SPEED 10.0
# define PIXEL_EFFECT
# define RES_X 40.0
# define RES_Y 22.0
# define BG_COLOR vec4(0.2)

// width of the cat (dont change this)
# define WIDTH 40.0

float pixel_effect(in vec2 uv, in vec2 ss) {

    vec2 p = 2.0*WIDTH*(uv - ss)*iResolution.xy/iResolution.x;
    vec2 q = clamp(p, -0.5, 0.5);

    float shade = 1.0 - length(p - q);
    shade = smoothstep(0.5, 0.6, shade);
    return shade;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy;
    vec3 res = iChannelResolution[0];

    vec2 new_res = vec2(RES_X, RES_Y);
    vec2 s_xy = (floor(new_res*uv) + 0.5)/new_res;
    vec2 ss = s_xy;


    // shift texture sample region in the y direction
    float shift_x = WIDTH*mod(ceil(SPEED*iTime), 6.0);
    float shift_y = 5.0;

    s_xy *= new_res.xy/res.xy;
    s_xy += vec2(shift_x, shift_y)/res.xy;

    vec4 color = texture(iChannel0, s_xy);

    if (color == vec4(1.0, 1.0, 1.0, 0.0)) {
        color = BG_COLOR;
    }

    #ifdef PIXEL_EFFECT
    float shade = pixel_effect(uv, ss);
    fragColor = shade*color;
    #else
    fragColor = color;
    #endif

}
