void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // 3D coordinates
    vec3 pos = vec3(uv*2.0-1.0, 0.5);

    // Rotation matrix
    mat3 rot = mat3(cos(iTime), -sin(iTime), 0,
                    sin(iTime),  cos(iTime), 0,
                    0,                  0,              1);

    // Rotate and project
    pos = rot * pos;
    pos.xy /= pos.z;

    // Calculate color
    vec3 col = vec3(0.5,0.5,0.5);
    if(abs(pos.x) < 0.5 && abs(pos.y) < 0.5) col = vec3(1.0,0.0,0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}
