float Hash21(vec2 p){
    p = fract(p*vec2(234.34,435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;//change starting point to center

    uv *= 10.;
    vec3 col = vec3(0);
    vec2 gv = fract(uv)-.5;//grid uv; fract(x)= x - floor(x);-.5 move to center
    vec2 id = floor(uv);

    float n = Hash21(id);//random number between 0 and 1


    float width = .25;
    if(n<.5) gv.x*= -1.;//reverse direction
    float d = abs(abs(gv.x+gv.y)-.5); //2 line in one grid
    float mask = smoothstep(.01, -.01, d-width);//-width,thickness

    col.gb += mask;
    col.gb += n;// more visual 3d/fake 3d


    fragColor = vec4(col,1.0);

}
