#define GAUSSIAN_PARAM 10

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec2 s  = 1./iResolution.xy;

    vec3 color = vec3(0.);
    float coef, totalCoef = 0.;

    for(int i=-GAUSSIAN_PARAM; i<=GAUSSIAN_PARAM ;i++)
        for(int j=-GAUSSIAN_PARAM; j<=GAUSSIAN_PARAM ;j++){
            coef = float( max(GAUSSIAN_PARAM-abs(i), GAUSSIAN_PARAM-abs(j)) );
            totalCoef += coef;
            color += texture(iChannel0,uv+vec2(i,j)*s).xyz * coef;
        }

    if(GAUSSIAN_PARAM == 0){ fragColor = texture(iChannel0,uv); }
    else{ fragColor = vec4(color/totalCoef,1.); }
}
