float sdBox(in vec2 p, in vec2 b)
{
    vec2 d = abs(p)-b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

void mainImage(out vec4 O, in vec2 C)
{
    vec2 r  = iResolution.xy,
         uv = C / r.xy;

    O = vec4(0.25 + uv.x * 0.5);

    uv = (uv * 2.0 - 1.0) * max(r.xy / r.yx, vec2(1.0));

    float p = 1.0 / min(r.x, r.y),
          t = sdBox(uv, vec2(1.0, 0.2));
          t = 1.0 - smoothstep(-p, p, t);

    O = mix(O, vec4(0.375), t);
    O.a = 1.0;
}
