// (adjusted) ENDESGA [223 chars]
#define C(x, y) length( cross( vec3( sin(x), sin(y), 1.5 ), D ) )

void mainImage( out vec4 c, vec2 f )
{
    float t = iTime*2.;
	vec3  R = iResolution, 
          D = vec3(f - R.xy*.5, R.y*.1);

    c = C(0., 0.) *
             vec4( C( 0. ,  t ),
                   C( t - .5 , 0. ),
                   C( t - 2. , t - 1. ), 1 ) / dot(D,D);

	c = exp( -c * c);
}

