void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    const float PI = 3.141593;
    float N =   iResolution.y;
    float L =  iResolution.y ;// 650.;
    float A =  PI * iTime;     
    vec2 fCoord = vec2(fragCoord.x -iResolution.x/2., fragCoord.y-iResolution.y/2.);

    float y0 = (fCoord.y * ( N + L)) /(N*sin(A) - fCoord.y*cos(A));
    float x0 = (N+L+y0*cos(A))* fCoord.x / N;

    vec2 newuv = (vec2(x0,y0) + iResolution.xy/2.)/iResolution.xy;
    if(newuv.x > 1. || newuv.y > 1. || newuv.x<0. || newuv.y < 0. /*|| L+y0*cos(A) < 0.*/) {
        fragColor= vec4(uv.x);
    }else {
        fragColor = texture(iChannel0,newuv);
    }
}
