void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Convert pixel coordinates to UV space
    vec2 uv = fragCoord / iResolution.xy;

    // Add time for animation
    float time = iTime * 0.5;

    // Create a color gradient based on UV and time
    vec3 color = vec3(
        0.5 + 0.5 * cos(time + uv.x + 0.1),  // Red component
        0.5 + 0.5 * cos(time + uv.y + 0.2),  // Green component
        0.5 + 0.5 * cos(time + (uv.x + uv.y) * 0.5)  // Blue component
    );

    // Add perfectly round scaling rings effect
    float rings = step(0.5, cos(time + length(uv - 0.5) * 20.0));
    color.rgb *= rings;

    // Apply color to the fragment
    fragColor = vec4(color, 1.0);
}
