void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Normorlize
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

    //Get the distance
    float d = length(uv);

    d=sin(d * iTime)/0.5;
    d=abs(d);
    d=smoothstep(0.0,0.1,d);

    fragColor = vec4(d,d,d,1.0);
}
