vec3 palette ( float t )
{
    vec3 a = vec3(0.778, -1.612, 0.108);
    vec3 b = vec3(-0.082, -0.422, -0.422);
    vec3 c = vec3(2.043, 3.173, 0.138);
    vec3 d = vec3(2.075, 2.409, 2.742);

    return a + b*cos(6.28318*(c*t*d));
}

float easeInSine(float x)
{
    return 1. - cos((x * 3.141593) / 3.);
}

float easeOutCubic(float x)
{
    return 1. - pow(1. - x, 3.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv0 = (fragCoord * 1.0 - iResolution.xy) / iResolution.y;
    uv0 = fract(uv0 * 16.);
    // vertical mirror
    uv0.x = abs(0.5-uv0.x);
    // horizontal mirror
	uv0.y = abs(0.5-uv0.y);

    uv0 = sin(uv0 - iTime * .5);


    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float d = uv.x;

    d = easeInSine(d) * .1;
    d = abs(d*4.) + .75;
    d = easeOutCubic(d) * 1.;

    d = sin(d * 2. + (iTime * 0.25));
    d = sin(d / 16.);
    d += length(uv);
    d -= length(uv0);

    vec3 col = palette(length(uv) * length(uv0));
    col *= d;

    fragColor = vec4(col, 1.0); 
    //fragColor = vec4(d,d,d,1.0);
}
