void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float distortionStrength = 1.;
    vec4 normal = texture(iChannel1, uv.xy + iTime/10.);
    vec2 displacement = clamp((normal.xy - .5) * distortionStrength, -1., 1.);
    vec4 background = texture(iChannel0, uv + displacement.xy);


    // Output to screen
    fragColor = background;
}

#define TEX iChannel0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    // Time varying pixel color
    uv += (texture(TEX,vec2(uv.y,iTime*0.5)).xy-0.5)*smoothstep(1.5,0.0,sin(iTime));
	uv += (texture(TEX,vec2(iTime*0.5,uv.x)).xy-0.5)*smoothstep(1.5,0.0,cos(iTime));

    vec3 color = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(iMouse.x/100., fragCoord.y, iMouse.y/100.));

    fragColor = vec4(color,1.);
}
