void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    const float speed = 2.;
    uv *= 2.0;

    vec4 col = vec4(0.0);

    vec2 position = -1. * vec2(mod(iTime* speed, 8.) - 4., 0.);

    float d = length(uv + position);
    float inverseD = 1. - d;

    col += smoothstep(0., 0.03, inverseD); 

    fragColor = col;
}
