void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Output to screen
    fragColor = texture(iChannel0, uv);
}

//this function simply animates the mouse movement
vec2 f_o_anim_h(float n_t){
    float n_parts = 3.0;
    float n_part_quotient = 1./n_parts;
    float n_t_modnor = mod(n_t, n_part_quotient) / n_part_quotient;
    float n_index = floor(n_t / n_part_quotient);

    vec2[] a_o = vec2[](
        vec2(0.0, n_t_modnor), 
        vec2(1.0, n_t_modnor), 
        vec2(n_t_modnor, 0.5)
    );

    return a_o[int(n_index)]-vec2(0.5,0.5);
}
vec2 f_o_anim_e(float n_t){
    float n_parts = 2.0;
    float n_part_quotient = 1./n_parts;
    float n_t_modnor = mod(n_t, n_part_quotient) / n_part_quotient;
    float n_index = floor(n_t / n_part_quotient);

    float n_rad = 0.5;
    float n_rot = 6.2831/4.;
    float n_radia = 6.2831 * 0.9;
    vec2[] a_o = vec2[](
        vec2(sin(n_t_modnor*-n_radia+n_rot)*n_rad,cos(n_t_modnor*-n_radia+n_rot)*n_rad), 
        vec2(n_t_modnor-0.5, 0.0)
    );

    return a_o[int(n_index)];
}
vec2 f_o_anim_l(float n_t){
    float n_parts = 1.0;
    float n_part_quotient = 1./n_parts;
    float n_t_modnor = mod(n_t, n_part_quotient) / n_part_quotient;
    float n_index = floor(n_t / n_part_quotient);

    vec2[] a_o = vec2[](
        vec2(0.5, n_t_modnor)
    );

    return a_o[int(n_index)]-vec2(0.5);
}
vec2 f_o_anim_o(float n_t){
    float n_parts = 1.0;
    float n_part_quotient = 1./n_parts;
    float n_t_modnor = mod(n_t, n_part_quotient) / n_part_quotient;
    float n_index = floor(n_t / n_part_quotient);
    float n_tau = 6.2831;
    float n_rad = 0.5;
    vec2[] a_o = vec2[](
        vec2(sin(n_t_modnor*n_tau)*n_rad,cos(n_t_modnor*n_tau)*n_rad)
    );

    return a_o[int(n_index)];
}
vec2 f_o_anim_exm(float n_t){
    float n_parts = 2.0;
    float n_part_quotient = 1./n_parts;
    float n_t_modnor = mod(n_t, n_part_quotient) / n_part_quotient;
    float n_index = floor(n_t / n_part_quotient);
    float n_rad = 0.1;
    float n_tau = 6.2831;
    vec2[] a_o = vec2[](
        vec2(0.0, 0.5-n_t_modnor*0.8),
        vec2(sin(n_t_modnor*n_tau)*n_rad,cos(n_t_modnor*n_tau)*n_rad)+vec2(0.0, - 0.6)
    );

    return a_o[int(n_index)];
}
vec2 f_o_mou_coo_nor(){
    float n_rad = 0.2;

    float n_index = floor(iTime);
    float n_t = fract(iTime);
    vec2[] a_o = vec2[](
        f_o_anim_h(n_t),
        f_o_anim_e(n_t),
        f_o_anim_l(n_t),
        f_o_anim_l(n_t),
        f_o_anim_o(n_t),
        f_o_anim_exm(n_t)
    );
    vec2 o = a_o[int(n_index)];
    float n_margin = 0.5;
    o = (o+vec2(n_index+(n_margin*n_index)-(float(a_o.length())/2.0), 0.0))*0.1;
    if(n_index > float(a_o.length())-1.){
        o = vec2(sin(iTime)*0.5,cos(iTime)*0.5);
    }
    return o;
    //vec2[] a_o_anim = vec2[] (
    //    vec2()
    //);
    //return vec2(sin(iTime)*n_rad, cos(iTime)*n_rad);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 o_pix_coo_nor = (fragCoord.xy - (iResolution.xy*0.5)) / iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - (iResolution.xy*0.5)) / iResolution.y;
    if(iMouse.w == 0.0){
        o_mou_coo_nor = f_o_mou_coo_nor();
    }
    vec4 o_col_last = texture(iChannel0, uv);

    float n_dist = length(o_pix_coo_nor-o_mou_coo_nor);
    float n_pen_size_nor = 0.005;
    float n_dist_smoothstep = smoothstep(n_pen_size_nor+(n_pen_size_nor*2.), n_pen_size_nor, n_dist);
    vec4 o_col_curr = vec4(n_dist_smoothstep);

    // by keeping only the higher color value we will overdraw everything
    float n_max = max(o_col_curr.r, o_col_last.r);

    fragColor = vec4(n_max);
}
