#define PI 3.1415926535


float loopRad = 10.0;
float clickIntensity = 1.0;
vec4 Blur(in sampler2D tex, in vec2 st, in vec2 stepSize, float blurIntensity)
{
    vec4 K = vec4(0.0);
    float total = 0.0;
    float filt_strength = 0.0;
    for(float i = -16.0; i < 17.0; i++)
    {
        for(float j = -16.0; j < 17.0; j++)
        {
             vec2 kernelPt = vec2(i,j);
             float r2 = dot(kernelPt, kernelPt);

             filt_strength = exp(-r2/ blurIntensity);

             total += filt_strength;

             K += filt_strength * texture(tex, st + stepSize * kernelPt);
        }
    }
    return K/total;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 kernelStepSize = 1.0 / iResolution.xy;
    vec2 uv_centered = uv - vec2(0.5);
    vec2 uv_mouse_centered = uv_centered - (iMouse.xy / iResolution.xy - vec2(0.5));
    float r2 = dot(uv_mouse_centered , uv_mouse_centered );
    float blur = 180.0 * r2 * clickIntensity;// * (1.0 + sin(iTime));

    fragColor = Blur(iChannel0, uv, kernelStepSize, blur);
}
