float leaves(vec2 p) {
    float r = 0.4 + 0.264*cos(10.0*atan(p.y, p.x) + 14.432*p.x + 1.032);
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = fragCoord.xy/iResolution.xy;
    st = 2.0 * st - 1.0;

    vec3 col = mix(vec3(1.000,0.436,0.012), vec3(0.995,0.849,0.625), sqrt((st.y+1.0)/2.0));

    st += vec2(0.290,-0.360);

    col = smoothstep(0.0, 0.04, leaves(st)) * col;
    float r = 0.03 + -0.006*cos(100.264*st.y) + exp(-30.192*(st.y+1.192)/1.488);
    col = max(smoothstep(r, r + 0.02, abs(st.x - 0.5*sin(st.y))), step(0.0, st.y)) * col;

    fragColor = vec4(col,1.0);
}
