float cir(vec2 uv, float r){
return float (length(uv) < r);
}
#define rot2D(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float curve(vec2 uv, float phase){

float divs = 42.;
float col;

    for(float j=0. ;j < 6.; j += 2./divs){

    float thick = (1./
    (pow(18.,sin(1.3*j+2.*iTime)-.3  )  + 3.1))/11.;


    col += cir(uv-j/(11.)+.811, thick) ;


    uv *= rot2D(

        .0016/thick * sin(j+iTime*(1.+phase/11.) + phase) / divs

        );

    }

    return col;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float minres = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord - .5*iResolution.xy) / minres;
    vec2 nMos =(iMouse.xy - .5*iResolution.xy) / minres;
    uv *= 1.8;

    vec3 col;

    for(float i = 0.; i < 11.; i++) {
        col += curve(uv*(1.+i/18.)+i/14.-0.5, i);
        uv *= rot2D(iTime/71.+11.04);
    }



    // Output to screen
    fragColor = vec4(sin(col/4.-1.2)*0.5+0.50,1.0);
}



