float nearRadian(vec2 uv, int intVal) {
    return smoothstep(0.3, 0., abs(mod(float(intVal)-atan(uv.y, uv.x)+3.14, 6.28)-3.14));
}

float radiusNearInt(vec2 uv) {
    return 1.-pow(abs(mod(2.*length(uv), 2.)-1.), 2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord-iResolution.xy/2.;
    uv = uv/iResolution.y*20.;
    float scaleFactor = 15.*(cos(iTime/20.*6.28)+1.);

    vec3 col = vec3(0.,0.,0.);

    uv*=scaleFactor;
    float x = length(uv);
    int intVal = int(floor(x));
    col += vec3(0.,1.,1.) * radiusNearInt(uv) * nearRadian(uv, intVal);

    fragColor = vec4(col, 1.0);
}
